sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

  var whatneeded = g_form.getValue('u_what_needed');
	//clear all of the choices from the what needed choice list
		g_form.clearOptions('u_what_needed');
//     if the value of what needed type is hr, add two hr choices and other to what needed field choice list
	if(newValue == 'hr') {
		g_form.addOption('u_what_needed','hr1','Human Resource1');
		g_form.addOption('u_what_needed', 'hr2','Human Resource2');
		g_form.addOption('u_what_needed', 'other', 'other');
// 		if the value of request type is facilities add two facilities type and other to what needed	
	}
  if(newValue == 'facilities'){
g_form.addOption('u_what_needed', 'facilities1', 'Facilities1');
g_form.addOption('u_what_needed', 'facilities2', 'Facilities2');
 g_form.addOption('u_what_needed', 'other', 'other');
	  // if the form is loading and it is not  a new record set u_what_needed value to the value from the record before it was loaded. 
	  if (isLoading || !g_form.isNewRecord()) {
    g_form.setValue('u_what_needed', whatneeded);
}
  }
}
